import react from "react";
import Message from './message';

const Thread = (props) => {
    return(
        <div>
            <h1>{props.title}</h1>
            {props.messages.map((message) => {return (<Message body={message.body} user={message.user} />)})}
        </div>
    )
}

export default Thread;
